import React, { useCallback, useState, useEffect } from 'react';
import { Drawer, Radio, Space, Table, message ,Button} from 'antd';

import { getListStorage } from '../../services/storageService';

function Storage() {

  getListStorage().then(console.log)
  const [list, setList] = useState([]);
  const [dataSource, setDataSource] = useState([]);
  const getData = useCallback(() => {
    //setLoading(true);
    getListStorage()
      .then((res) => {
        //const listAsssignmentId = res.data.assignmentIdReq;
        //setList(listAsssignmentId);
        const convertedData = res.data.map((data) => {
          const {
            productId,
            productName,
            quantity,
            unitName,
          } = data;

          return {
            key: productId,
            productId,
            productName,
            quantity,
            unitName,
          };
        });
        setDataSource(convertedData);
        //setTotalElements(res.data.totalElements);
      })
      .catch((error) => {
        message.error(error.message);

      });

    //setLoading(false);
  }, []);
  useEffect(() => {
    getData();
  }, [getData]);

  const [open, setOpen] = useState(false);
  const [placement, setPlacement] = useState('right');
  const showDrawer = () => {
    setOpen(true);
  };
  const onChange = (e) => {
    setPlacement(e.target.value);
  };
  const onClose = () => {
    setOpen(false);
  };
  let displayIdCounter = 1;
  const columns = [
    {
      title: 'ProductId',
      dataIndex: 'productId',
      key: dataSource.productId,
      render: () => displayIdCounter++,
    },
    {
      title: 'Product Name',
      dataIndex: 'productName',
      key: dataSource.productName,
      sorter: {
        compare: (a, b) => a.productName - b.productName,
        multiple: 3,
      },
    },
    {
      title: 'Quantity',
      dataIndex: 'quantity',
      key: dataSource.quantity,
      sorter: {
        compare: (a, b) => a.quantity - b.quantity,
        multiple: 2,
      },
    },
    {
      title: 'Unit Name',
      dataIndex: 'unitName',
      key: dataSource.unitName,
      sorter: {
        compare: (a, b) => a.unitName - b.unitName,
        multiple: 1,
      },
    },
  ];
  
  const onChangeTable = (pagination, filters, sorter, extra) => {
    console.log('params', pagination, filters, sorter, extra);
  };

  return (
    <>

      <Space>
        <Radio.Group value={placement} onChange={onChange}>
          <Radio value="top">top</Radio>
          <Radio value="right">right</Radio>
          <Radio value="bottom">bottom</Radio>
          <Radio value="left">left</Radio>
        </Radio.Group>
        <Button type="primary" onClick={showDrawer}>
          Open
        </Button>
      </Space>
      <Drawer
        title="Drawer with extra actions"
        placement={placement}
        width={500}
        onClose={onClose}
        open={open}
        extra={
          <Space>
            <Button onClick={onClose}>Cancel</Button>
            <Button type="primary" onClick={onClose}>
              OK
            </Button>
          </Space>
        }
      >
        <p>Some contents...</p>
        <p>Some contents...</p>
        <p>Some contents...</p>
      </Drawer>

      <Table columns={columns} dataSource={dataSource} onChange={onChangeTable} />;


    </>
  );
};

export default Storage;
