import React, { useCallback,useState,useEffect } from 'react';
import { Button ,Table} from 'antd';

import { getListStorage } from '../../services/storageService';

function Storage() {
    
    //getListStorage().then(console.log)
    const [list, setList] = useState([]);
    const [dataSource, setDataSource] = useState([]);
    const getData = useCallback(() => {
      //setLoading(true);
      getListStorage()
        .then((res) => {
          //const listAsssignmentId = res.data.assignmentIdReq;
          //setList(listAsssignmentId);
          const convertedData = res.map((data) => {
            const {
              productId,
              productName,
              quantity,
              unitName,
              //supplier,
            } = data;
  
            return {
              key: productId,
              productName,
              quantity,
              unitName
             // supplierId: `${supplier.supplierId}`,
              //supplierName: `${supplier.supplierName}`,

            };
          });
          setDataSource(convertedData);
          //setTotalElements(res.data.totalElements);
        })
        .catch((error) => {
          //message.error(error.message);
          console.log(error)
        });
  
      //setLoading(false);
    }, []);
    useEffect(() => {
      getData();
    }, [getData]);

    //console.log(getData())
    console.log("data"+dataSource)

    const columns = [
        {
          title: 'Name',
          dataIndex: 'name',
        },
        {
          title: 'Chinese Score',
          dataIndex: 'chinese',
          sorter: {
            compare: (a, b) => a.chinese - b.chinese,
            multiple: 3,
          },
        },
        {
          title: 'Math Score',
          dataIndex: 'math',
          sorter: {
            compare: (a, b) => a.math - b.math,
            multiple: 2,
          },
        },
        {
          title: 'English Score',
          dataIndex: 'english',
          sorter: {
            compare: (a, b) => a.english - b.english,
            multiple: 1,
          },
        },
      ];
      const data = [
        {
          key: '1',
          name: 'John Brown',
          chinese: 98,
          math: 60,
          english: 70,
        },
        {
          key: '2',
          name: 'Jim Green',
          chinese: 98,
          math: 66,
          english: 89,
        },
        {
          key: '3',
          name: 'Joe Black',
          chinese: 98,
          math: 90,
          english: 70,
        },
        {
          key: '4',
          name: 'Jim Red',
          chinese: 88,
          math: 99,
          english: 89,
        },
      ];
      const onChange = (pagination, filters, sorter, extra) => {
        console.log('params', pagination, filters, sorter, extra);
      };

  return (
    <>
     <Table columns={columns} dataSource={data} onChange={onChange} />;
    </>
  );
};

export default Storage;
