import React, { useCallback, useState, useEffect } from 'react';
import { Drawer, Radio, Space, Table, message ,Button} from 'antd';

import { getListStorage ,getSupllierStorage} from '../../services/storageService';

function Storage() {

  getListStorage().then(console.log)
  const [list, setList] = useState([]);
  const [dataSuplier, setDataSuplier] = useState([]);
  const [dataSource, setDataSource] = useState([]);
  const getData = useCallback(() => {
    //setLoading(true);
    getListStorage()
      .then((res) => {
        //const listAsssignmentId = res.data.assignmentIdReq;
        //setList(listAsssignmentId);
        const convertedData = res.data.map((data) => {
          const {
            productId,
            productName,
            quantity,
            unitName,
          } = data;

          return {
            key: productId,
            productId,
            productName,
            quantity,
            unitName,
          };
        });
        setDataSource(convertedData);
        //setTotalElements(res.data.totalElements);
      })
      .catch((error) => {
        message.error(error.message);

      });

    //setLoading(false);
  }, []);
  useEffect(() => {
    getData();
  }, [getData]);

  const [open, setOpen] = useState(false);
  const showDrawer = () => {
    setOpen(true);
  };
  const onClose = () => {
    setOpen(false);
  };


  let displayIdCounter = 1;
  const columns = [
    {
      title: 'ProductId',
      dataIndex: 'productId',
      key: dataSource.productId,
      render: () => displayIdCounter++,
    },
    {
      title: 'Product Name',
      dataIndex: 'productName',
      key: dataSource.productName,
      sorter: {
        compare: (a, b) => a.productName - b.productName,
        multiple: 3,
      },
      onCell: () => {
				return {
					onClick: () => {
						showDrawer();
					},
				};
			},
    },
    {
      title: 'Quantity',
      dataIndex: 'quantity',
      key: dataSource.quantity,
      sorter: {
        compare: (a, b) => a.quantity - b.quantity,
        multiple: 2,
      },
    },
    {
      title: 'Unit Name',
      dataIndex: 'unitName',
      key: dataSource.unitName,
      sorter: {
        compare: (a, b) => a.unitName - b.unitName,
        multiple: 1,
      },
    },
  ];
  
  const onChangeTable = (pagination, filters, sorter, extra) => {
    console.log('params', pagination, filters, sorter, extra);
  };

  return (
    <>

       <Button type="primary" onClick={showDrawer}>
        Open
      </Button>
      <Drawer title="Basic Drawer" placement="right" onClose={onClose} open={open}>
        <p>{dataSource.productName}</p>
        <p>Some contents...</p>
        <p>Some contents...</p>
      </Drawer>
  

      <Table columns={columns} dataSource={dataSource} onChange={onChangeTable} />;


    </>
  );
};

export default Storage;
