import React, { useCallback,useState,useEffect } from 'react';
import { Button ,Table,message} from 'antd';

import { getListStorage } from '../../services/storageService';

function Storage() {
    
    getListStorage().then(console.log)
    const [list, setList] = useState([]);
    const [dataSource, setDataSource] = useState([]);
    const getData = useCallback(() => {
      //setLoading(true);
      getListStorage()
        .then((res) => {
          //const listAsssignmentId = res.data.assignmentIdReq;
          //setList(listAsssignmentId);
          const convertedData = res.data.map((data) => {
            const {
              productId,
              productName,
              quantity,
              unitName,
            } = data;
  
            return {
              key: productId,
              productId,
              productName,
              quantity,
              unitName,
            };
          });
          setDataSource(convertedData);
          //setTotalElements(res.data.totalElements);
        })
        .catch((error) => {
          message.error(error.message);
          
        });
  
      //setLoading(false);
    }, []);
    useEffect(() => {
      getData();
    }, [getData]);

    //console.log(getData())
    //console.log("data"+dataSource)
    
    const columns = [
        {
          title: 'ProductId',
          dataIndex: 'productId',
          key: dataSource.productId,
          render: () => displayIdCounter++,
        },
        {
          title: 'Product Name',
          dataIndex: 'productName',
          key: dataSource.productName,
          sorter: {
            compare: (a, b) => a.productName - b.productName,
            multiple: 3,
          },
        },
        {
          title: 'Quantity',
          dataIndex: 'quantity',
          key:dataSource.quantity,
          sorter: {
            compare: (a, b) => a.quantity - b.quantity,
            multiple: 2,
          },
        },
        {
          title: 'Unit Name',
          dataIndex: 'unitName',
          key:dataSource.unitName,
          sorter: {
            compare: (a, b) => a.unitName - b.unitName,
            multiple: 1,
          },
        },
      ];
      const data = [
        {
          key: '1',
          name: 'John Brown',
          chinese: 98,
          math: 60,
          english: 70,
        },
        {
          key: '2',
          name: 'Jim Green',
          chinese: 98,
          math: 66,
          english: 89,
        },
        {
          key: '3',
          name: 'Joe Black',
          chinese: 98,
          math: 90,
          english: 70,
        },
        {
          key: '4',
          name: 'Jim Red',
          chinese: 88,
          math: 99,
          english: 89,
        },
      ];
      const onChange = (pagination, filters, sorter, extra) => {
        console.log('params', pagination, filters, sorter, extra);
      };

  return (
    <>
     <Table columns={columns} dataSource={dataSource} onChange={onChange} />;
    </>
  );
};

export default Storage;
